    }
    function rounD(num){
        return Math.round(100 * num) / 100;
    }
    function dateToString(date){
        date = date.toUTCString().split(' ');
        return date.slice(0, 4).join(' ');
    }
    function getQuote(){
        var from = {
            country: $('#from select[name="country"]').val(),
            state: $('#from select[name="state"]').val(),
            city: $('#from select[name="city"]').val(),
        };
        var to = {
            country: $('#to select[name="country"]').val(),
            state: $('#to select[name="state"]').val(),
            city: $('#to select[name="city"]').val(),
        };
        var currency = $('select[name="currency"]').val();
        var unit = $('select[name="unit"]').val();

        var length = $('#dimensions input[name="length"]').val();
        var width = $('#dimensions input[name="width"]').val();
        var height = $('#dimensions input[name="height"]').val();
        if(!length || !width || !height){toast('Specify package dimensions', 'danger', 3000); return;}
        var weight = $('#package-dim input[name="weight"]').val();
        if(!weight){toast('Specify package weight', 'danger', 3000); return;}

        if(unit == 'kg/cm'){
            length *= .393701;
            width *= .393701;
            height *= .393701;
            weight *= 2.20462;
        }
        var volume = length * width * height;
        var dim = volume / 139 > weight ? volume / 139 : weight;
        declared_value = $('#package-dim input[name="declared-value"]').val();
        $.ajax('./get-quote.php',{
            method: 'POST',
            data: {
                from: from,
                to: to,
                currency: currency,
                dim: dim,
                declared_value: declared_value
            }
        }).done(function(data){
            var data = JSON.parse(data);
            console.log({data, constants})
            var time = constants.time[from.country + to.country];
            var R = constants.R[from.country + to.country];
            if(R.unit == 'kg'){weight *= .45}
            var stdD = new Date();
            var expD = new Date();
            var seaD = new Date();
            stdD.setDate(stdD.getDate() + parseInt(time.standard.split('-')[1]));
            expD.setDate(expD.getDate() + parseInt(time.express.split('-')[1]));
            seaD.setDate(seaD.getDate() + parseInt(time.sea.split('-')[1]) * 7);
            var standard = '<div class="card-header"><center>Standard Shipping</center></div><div class="card-body">    <center><h3 class="card-title">' + (currency == 'USD' ? '$' : currency == 'NGN' ? '₦' : currency == 'GBP' ? '£' : '') + rounD(currency == 'USD' ? data.standard : currency == 'NGN' ? data.standard * 360 : currency == 'GBP' ? data.standard * .76 : '') + '</h3>    <h6>' + time.standard + ' Business Days</h6></center>    <p class="card-text">Estimated Delivery: ' + dateToString(stdD) + '</p>    <p class="card-text">Your delivery cost is ' + (currency == 'USD' ? '$' : currency == 'NGN' ? '₦' : currency == 'GBP' ? '£' : '') + (currency == 'USD' ? R.standard : currency == 'NGN' ? rounD(R.standard * 360) : currency == 'GBP' ? rounD(R.standard * .76) : 0) + '/' + (R.unit == 'lbs' ? 'pound' : 'kilogram') + '</p></div><a href="#" class="btn btn-primary">Lower shipping cost</a>\n            ';
            var hazadous = '<div class="card-header"><center>Hazmat Shipping</center></div><div class="card-body">    <center><h3 class="card-title">' + (currency == 'USD' ? '$' : currency == 'NGN' ? '₦' : currency == 'GBP' ? '£' : '') + rounD(currency == 'USD' ? data.hazadous : currency == 'NGN' ? data.hazadous * 360 : currency == 'GBP' ? data.hazadous * .76 : '') + '</h3>    <h6>' + time.standard + ' Business Days</h6></center>    <p class="card-text">Estimated Delivery: ' + dateToString(stdD) + '</p>    <p class="card-text">Your delivery cost is ' + (currency == 'USD' ? '$' : currency == 'NGN' ? '₦' : currency == 'GBP' ? '£' : '') + (currency == 'USD' ? rounD(data.hazadous / weight / 1.05) : currency == 'NGN' ? rounD(data.hazadous / weight / 1.05 * 360) : currency == 'GBP' ? rounD(data.hazadous / weight / 1.05 * .76) : 0) + '/' + (R.unit == 'lbs' ? 'pound' : 'kilogram') + '</p></div><a href="#" class="btn btn-primary">Lower shipping cost</a>\n            ';
            var express = '<div class="card-header"><center>Express Shipping</center></div><div class="card-body">    <center><h3 class="card-title">' + (currency == 'USD' ? '$' : currency == 'NGN' ? '₦' : currency == 'GBP' ? '£' : '') + rounD(currency == 'USD' ? data.express : currency == 'NGN' ? data.express * 360 : currency == 'GBP' ? data.express * .76 : '') + '</h3>    <h6>' + time.express + ' Business Days</h6></center>    <p class="card-text">Estimated Delivery: ' + dateToString(expD) + '</p>    <p class="card-text">Your delivery cost is ' + (currency == 'USD' ? '$' : currency == 'NGN' ? '₦' : currency == 'GBP' ? '£' : '') + (currency == 'USD' ? R.express : currency == 'NGN' ? rounD(R.express * 360) : currency == 'GBP' ? rounD(R.express * .76) : 0) + '/' + (R.unit == 'lbs' ? 'pound' : 'kilogram') + '</p></div><a href="#" class="btn btn-primary">Lower shipping cost</a>\n            ';
            var sea = '<div class="card-header"><center>Sea Shipping</center></div><div class="card-body"><center><h3 class="card-title">' + (currency == 'USD' ? '$' : currency == 'NGN' ? '₦' : currency == 'GBP' ? '£' : '') + rounD(currency == 'USD' ? data.sea : currency == 'NGN' ? data.sea * 360 : currency == 'GBP' ? data.sea * .76 : '') + '</h3><h6>' + time.sea + ' Weeks</h6></center><p class="card-text">Estimated Delivery: ' + dateToString(seaD) + '</p><p class="card-text">Your delivery cost is ' + (currency == 'USD' ? '$' : currency == 'NGN' ? '₦' : currency == 'GBP' ? '£' : '') + (currency == 'USD' ? R.sea : currency == 'NGN' ? rounD(R.sea * 360) : currency == 'GBP' ? rounD(R.sea * .76) : 0) + '/' + (R.unit == 'lbs' ? 'pound' : 'kilogram') + '</p></div><a href="#" class="btn btn-primary">Lower shipping cost</a>';
            $('#standard').html(standard);
            $('#hazadous').html(hazadous);
            $('#express').html(express);
            $('#sea').html(sea);
            $('#standard').removeClass('hidden');
            $('#hazadous').removeClass('hidden');
            $('#express').removeClass('hidden');
            $('#sea').removeClass('hidden');
        });
    }
    function setStatesOrCities(jq_str, states_or_cities){
        replacement = (states_or_cities.map(function(state_or_city, i){
            if(i == 0){
                return '<option value="' + state_or_city + '" selected>' + state_or_city.replace(/(state|city)/i, '') + '<\/option>';
            }
            return '<option value="' + state_or_city + '">' + state_or_city.replace(/(state|city)/i, '') + '<\/option>';
        }).join('\n'));
        $(jq_str).html(replacement);
        $(jq_str).trigger('change');
    }
    $('#from select[name="country"]').change(function(){
        var code = $(this).val();
        var states = countries.find(function(country){
            return country.code == code;
        }).regions.map(function(region){return region.name});
        setStatesOrCities('#from select[name="state"]', states);
        if(code == 'ng'){
            $('#to select[name="country"]').html('<option value="us" selected>United States</option><option value="gb">UK</option>');
        } else if(code == 'us'){
            $('#to select[name="country"]').html('<option value="ng" selected>Nigeria</option>');
        } else if(code == 'gb'){
            $('#to select[name="country"]').html('<option value="ng" selected>Nigeria</option>');
        }
        $('#to select[name="country"]').trigger('change');
        
    });
    $('#to select[name="country"]').change(function(){
        var code = $(this).val();
        var states = countries.find(function(country){
            return country.code == code;
        }).regions.map(function(region){return region.name});
        setStatesOrCities('#to select[name="state"]', states);
    });
    $('#from select[name="state"]').change(function(){
        var state = $(this).val();
        var code = $('#from select[name="country"]').val();
        var cities = countries.find(function(country){
            return country.code == code;
        }).regions.find(function(region){
            return region.name == state;
        }).cities;
        setStatesOrCities('#from select[name="city"]', cities);
    });
    $('#to select[name="state"]').change(function(){
        var state = $(this).val();
        var code = $('#to select[name="country"]').val()
        var cities = countries.find(function(country){
            return country.code == code;
        }).regions.find(function(region){
            return region.name == state;
        }).cities;
        setStatesOrCities('#to select[name="city"]', cities);
    });
    $(document).ready(function(){
        var used_countries = [
            {name: "United States", code: "us"},
            {name: "Nigeria", code: "ng"},
            {name: "UK", code: "gb"}
        ].map(function(country, i){
            return '<option value="' + country.code + '">' + country.name + '</option>';
        });
        $('select[name="country"]').html(used_countries);
        $('#from select[name="country"]').val('us');
        $('#from select[name="country"]').trigger('change');
        $('body').css('background', constants.color.background);
        $('#info input, select').css('background', constants.color.inputs);
        $('#info *').css('color', constants.color.text);
        $('.card .card-header,a').css('background', constants.color.cool);
        $('#shipping-quote').css('background', constants.color.cool);
    })
</script>
</html>